import React, { useState } from 'react';
import axios from 'axios';

const VlogDisplay: React.FC = () => {
  const [videoUrl, setVideoUrl] = useState<string>('');
  const [message, setMessage] = useState<string>('');

  const fetchVlog = async () => {
    setMessage('正在获取视频，请稍候...');
    try {
      const response = await axios.post('https://your-backend-url.com/api/weddingcoreVlog', {
        userId: 'exampleUserId', // 示例用户ID
        sessionId: 'exampleSessionId', // 示例会话ID
      });
      setVideoUrl(response.data.data);
      setMessage('');
    } catch (error) {
      setMessage('获取视频失败，请重试。');
    }
  };

  return (
    <div className="vlog-display">
      <h2>AI 婚纱Vlog</h2>
      <button onClick={fetchVlog}>获取Vlog视频</button>
      {message && <p>{message}</p>}
      {videoUrl && (
        <video controls className="vlog-video">
          <source src={videoUrl} type="video/mp4" />
          您的浏览器不支持视频标签。
        </video>
      )}
    </div>
  );
};

export default VlogDisplay;
